    // TEACHER ENROLL
    private boolean jButton3MouseClicked(java.awt.event.MouseEvent evt) {
        // TODO add your handling code here:
        //TeacherName teacherPassword teacherId teacherDepart
        // teacherOop teacherDld teacherCom teacherIr teacherOb teacherStats
        // jLabel13
        jLabel13.setText("");
        MyFiler.readFile(teachers);
        jLabel7.setText("");
//        Teacher teachers[] = new Teacher[50];
//        MyFiler.readFile(teachers);
        if(TeacherName.getText().compareTo("") == 0 || teacherId.getText().compareTo("") == 0 || teacherDepart.getText().compareTo("") == 0|| teacherPassword.getText().compareTo("") == 0){
            jLabel13.setText("Fill all fields");
            return false;
        }
        if(!(currentUser.isValidPassword(teacherPassword.getText()))){
            jLabel13.setText("Password Not Strong");
            return false;
        }

        try{
            for(int i=0; i<50; i++){
                if(teachers[i].id.compareTo(teacherId.getText()) == 0){
                    jLabel13.setText("User already exist");
                    return false;
                }
            }
        } catch(Exception e){
            System.out.println(e);
        }

        Teacher newObj = new Teacher(teacherId.getText(),teacherPassword.getText(),TeacherName.getText(),teacherDepart.getText());

        if(teacherOop.isSelected()){
            newObj.assignCourse(new Course("OOP","o1201"));
        }
        if(teacherDld.isSelected()){
            newObj.assignCourse(new Course("DLD","d1211"));
        }
        if(teachercom.isSelected()){
            newObj.assignCourse(new Course("COM","c1102"));
        }
        if(teacherStats.isSelected()){
            newObj.assignCourse(new Course("STATS","s1221"));
        }
        if(teacherOb.isSelected()){
            newObj.assignCourse(new Course("OB","oB101"));
        }
        if(teacherIr.isSelected()){
            newObj.assignCourse(new Course("IR","i3210"));
        }

        if(MyFiler.myAppendObject(newObj,teachers)){
            teachercom.setSelected(false); teacherOb.setSelected(false); teacherIr.setSelected(false); teacherStats.setSelected(false);teacherDld.setSelected(false);teacherOop.setSelected(false);
            TeacherName.setText(""); teacherPassword.setText(""); teacherId.setText(""); teacherDepart.setText("");
            JOptionPane.showMessageDialog(null,"Account Created");
            return true;
        }



        return true;
    }

