    //  REMOVE STUDENT(STUDENT ID)
    private void jButton5MouseClicked(java.awt.event.MouseEvent evt) {
        // TODO add your handling code here:
        stdRemoved.setText("");
        Student students[] = new Student[50];
        boolean deleted = false;
        MyFiler.readFile(students);
        try{
            for(int i=0; i<50; i++){
                if(removeStdId.getText().compareTo(students[i].id) == 0){
                    MyFiler.deleteObject(students[i],students);
                    deleted = true;
                    break;
                }
            }

        } catch (Exception e){
            System.out.println(e);
        }

        if(deleted){
            stdRemoved.setForeground(Color.red);
            stdRemoved.setText("Student Removed");
        }
    }
    //  SEARCH STUDENT
    private void jButton4MouseClicked(java.awt.event.MouseEvent evt) {
        // TODO add your handling code here:
        removeStdId.getText();
        removeStdName.setForeground(Color.black);
        Student students[] = new Student[50];
        boolean found = false;
        MyFiler.readFile(students);
        try{
            for(int i=0; i<50; i++){
                if(removeStdId.getText().compareTo(students[i].id) == 0){
                    found = true;
                    removeStdName.setText(students[i].name);
                    break;
                }
            }

        } catch (Exception e){
            System.out.println(e);

        }

        if(!found){
            removeStdName.setForeground(Color.red);
            removeStdName.setText("User not Found");
        }
    }

